import { rainlink } from "..";
import { getVolume } from "../lib";

export const waitingEmbed = async (guildId: string) => {
  const loop = () => {
    switch (rainlink.players.get(guildId)?.loop) {
      case "song":
        return "Song";
      case "queue":
        return "Queue";
      default:
        return "None";
    }
  };
  let volume = "";
  const player = rainlink.players.get(guildId);
  if (!player) {
    const dbVolume = await getVolume(guildId);
    if (dbVolume) {
      volume = `${dbVolume}%`;
    } else {
      volume = "40%";
    }
  } else {
    `${player.volume}%`;
  }
  return {
    color: 16752324,
    title: "Waiting for something to play",
    description:
      "To play something send link or name to this channel or use `/play`",
    image: {
      url: "https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/5c60f840-691b-4fcf-83a4-55ec348ca9bf/di6ip78-a257e921-f3cd-4a0a-b4c6-e9cbb74b4987.png/v1/fill/w_894,h_894,q_70,strp/snorlax__the_sleeping_pokemon_by_huxianshi_di6ip78-pre.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..S3pHMHrqRszHg_O4XXZmz3ZVXA8nrWLMm4feJlAxkFs",
    },
    fields: [
      {
        name: "Volume",
        value: volume,
        inline: true,
      },
      {
        name: "** **",
        value: "** **",
        inline: true,
      },
      {
        name: "Loop",
        value: loop(),
        inline: true,
      },
    ],
  };
};
